import { Component, ComponentFactory, ComponentFactoryResolver, OnInit, ViewChild, ViewContainerRef } from '@angular/core';
import { WidgetService } from './services/widget.service';
import { BlueComponent } from './blue/blue.component';
import { RedComponent } from './red/red.component';
import { YellowComponent } from './yellow/yellow.component';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnInit {
  blueFactory!: ComponentFactory<BlueComponent>;
  redFactory!: ComponentFactory<RedComponent>;
  yellowFactory!: ComponentFactory<YellowComponent>;
  @ViewChild('dynamic') viewContainerRef!: ViewContainerRef;


  constructor(
    private widgetService: WidgetService,
    private resolver: ComponentFactoryResolver
  ) {}

  ngOnInit(): void {
    this.listWidgets()

    // const aaa = this.loadDynamicComponent('blue');
    // console.log('aaa:', aaa)

    this.blueFactory = this.resolver.resolveComponentFactory(BlueComponent);
    this.redFactory = this.resolver.resolveComponentFactory(RedComponent);
    this.yellowFactory = this.resolver.resolveComponentFactory(YellowComponent);
  }

  listWidgets() {
    this.widgetService.listWidgets('assets/widgets.json').subscribe(data => {
      console.log(data)
    });
  }

  loadDynamicWithComponentFactoryResolver(type: string): void {
    switch (type) {
      case "blue":
        this.viewContainerRef.createComponent(this.blueFactory);
        break;
      case "red":
        this.viewContainerRef.createComponent(this.redFactory);
        break;
      case "yellow":
        this.viewContainerRef.createComponent(this.yellowFactory);
        break;
      default:
        return
    }

  }

  // loadDynamicComponent(type: string): any {
  private async loadDynamicComponent2(type: string): Promise<any> {
    // const widgetModule = await import(`./${type}/${type}.module`)
    // return new widgetModule.BlueModule.BlueComponent

    // const widgetModule = await import(`./blue/blue.module`)
    // const widgetModule = await import(`./blue/blue.component`)
    // const hoge = new widgetModule.BlueComponent
  }

}

<div class="m-24 flex flex-wrap gap-x-2 justify-center">
  <button
    class="
      py-2
      px-4
      border border-transparent
      shadow-sm
      text-sm
      font-medium
      rounded-md
      text-white
      bg-blue-400
      hover:bg-blue-600
    "
    (click)="loadDynamicWithComponentFactoryResolver('blue')"
  >
    Add Blue Component
  </button>

  <button
    class="
      py-2
      px-4
      border border-transparent
      shadow-sm
      text-sm
      font-medium
      rounded-md
      text-white
      bg-yellow-400
      hover:bg-yellow-600
    "
    (click)="loadDynamicWithComponentFactoryResolver('yellow')"
  >
    Add Yellow Component
  </button>

  <button
    class="
      py-2
      px-4
      border border-transparent
      shadow-sm
      text-sm
      font-medium
      rounded-md
      text-white
      bg-red-400
      hover:bg-red-600
    "
    (click)="loadDynamicWithComponentFactoryResolver('red')"
  >
    Add Red Component
  </button>
</div>

